import { Box, Wind, Printer, Microchip, Code, CheckCircle, ArrowRight, Zap, Sparkles, Lightbulb, Play, Pause, Star, Shield, FileText, Headphones, ChevronRight, Target, TrendingUp, Users, Award } from "lucide-react";
